import React, {Component} from 'react';
import Moment from 'moment';
import { Redirect } from 'react-router';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import logo from './logo.png';
import './App.css';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import PurchaseTicket from './components/purchaseTicket';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';



class App extends React.Component {
	constructor(props){
		super(props)
  this.state = {
    users:[],
    events: [],
    isLoading: true,
    errors: null,
	};
}



  // Now we're going to make a request for data using axios
  getPosts() {
    axios
			.get("http://localhost:3210/users")
      .then(response => {
				console.log(response.data)
      })
      .then(this.processResults)

      .then(events => {
        console.log(events)
        this.setState({
          events,
          isLoading: false,
        });
        // console.log(this.state)
      })
      .catch(error => this.setState({ error, isLoading: false }));   
  }
  processResults(data) {

    const raceId_arr = data.map(d => d.id);
    const season_arr = data.map(d => d.event-date);
    const raceName_arr = data.map(d => d.eve);
    const url_arr = data.map(d => d.url);
    const data_mapped = {'raceId': raceId_arr, 'season': season_arr, 'raceName': raceName_arr, 'url': url_arr};

    this.setState({races:data_mapped});
    console.log(data_mapped);
    console.log(this.state.races);
  }

  componentDidMount() {
    this.getPosts();
	}



  render() {
    const { isLoading, users , events} = this.state;

    return (
      <React.Fragment>
        <Router>
        <div>    
          {!isLoading ?
          (
            <Carousel showThumbs={false}>
                {	events && events.map(event=>{
                  return(

                    <div key={event.id}>
                        <img className="mySlides img-responsive" src={event.cover_image} style={{width: "80%", height: 550}}/>
                        <Link to={{pathname: '/purchaseTicket',state: {myEvent:event}} }>

                            <button className="explore">Purchase Ticket</button>
                          </Link>
                          <button className="w3-button w3-black w3-display-left" onClick="plusDivs(-1)">&#10094;</button>
                          <button className="w3-button w3-black w3-display-right" onClick="plusDivs(1)">&#10095;</button>
                      </div>

                  );

                })	 }

            </Carousel> )  :(

              <a href="http://dashboard.tickets4u.co.ke/index.php/login">
		            <img className="mySlides img-responsive" src="assets/images/default.jpg"/> 
              </a>

          )
            }
            <Switch>
              <Route exact path='/home'  />
              <Route path='/purchaseTicket' component={PurchaseTicket} />

          </Switch>
        </div>
        </Router>
      </React.Fragment>
    );
  }
}
export default App;
