import React, {Component} from 'react';
import Moment from 'moment';
import { Redirect } from 'react-router';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import logo from './logo.png';
import './App.css';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import PurchaseTicket from './components/purchaseTicket';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';



class App extends React.Component {
	constructor(props){
		super(props)
  this.state = {
    events: [],
    isLoading: true,
    errors: null,
	};
}



  // Now we're going to make a request for data using axios
  getPosts() {
    axios
			.get("http://localhost:3210/users")
      .then(response => {
				console.log(response.data)
        return axios
        .get("http://localhost:3210/data")
        .then(response => {
          return response.data.map(event=> ({
            event_id: `${event.event_id}`,
            id: `${event.id}`,
            cover_image: `${event.cover_image}`
          }))
        })
      })


      .then(events => {
        console.log(events)
        this.setState({
          events,
          isLoading: false,
        });
        // console.log(this.state)
      })
      .catch(error => this.setState({ error, isLoading: false }));   
  }

  componentDidMount() {
    this.getPosts();
	}



  render() {
    const { isLoading, users , events} = this.state;

    return (
      <React.Fragment>
        <Router>
        <div>    
          {!isLoading ?
          (
            <Carousel showThumbs={false}>
                {	users && users.map(user=>{
                  if(user.event_status==1 && Moment()<Moment(user.event_date))
                 { return(
                   <div>
                    <p>{user.id}</p>

                    </div>
                  );}
                  else{
                    return( <a href="http://dashboard.tickets4u.co.ke/index.php/login">
                    <img class="mySlides img-responsive" src="assets/images/default.jpg"/></a>);
                  }

                })	 }

            </Carousel> )  
            :(<p>Loading</p>)
            }
            <Switch>
              <Route exact path='/home'  />
              <Route path='/purchaseTicket' component={PurchaseTicket} />

          </Switch>
        </div>
        </Router>
      </React.Fragment>
    );
  }
}
export default App;
