import React, {Component} from 'react';
import Moment from 'moment';
import { Redirect } from 'react-router';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';

export default class PurchaseTicket extends Component {
    constructor(props){
        super(props)

const myEvent=this.props.location.state && this.props.location.state.myEvent;
    console.log(myEvent[0])
}
    render() {
        return (
            <div className="jumbotron">
                <section className="section-page-content">
                    <div class="container">
                        <div class="row">
                            <div class="col-md-12">
                                <div class="section-order-details-event-title">
                                    <div class="section-header">
                                        <h2 class="event-title"><strong>{this.props.location.state.myEvent.id}</strong></h2>
                                    </div>
                                </div>
                                <div class="col-md-4">
                                    <div class="section-order-details-event-title">   
                                        <img class="event-img" src={this.props.location.state.myEvent[0].cover_image}/>
                                    </div>
                                </div>
                                <div class="col-md-8">
                                    <div class="section-order-details-event-info">
                                        <div class="venue-details">
                                            <div class="venue-details-info">
                                                <h3>Venue & Event Information</h3>
                                                <p>Venue:{this.props.location.state.myEvent[0].event_id}</p>
                                            </div>
                                        </div>
                                        <div class="seat-details">
                                            <div class="seat-details-info">
                                                <h3>Event Information</h3>
                                                <p>{this.props.location.state.myEvent[0].event_description}</p>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        )
    }
}
