import React, {Component} from 'react';
import Moment from 'moment';
import { Redirect } from 'react-router';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import logo from './logo.png';
import './App.css';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import PurchaseTicket from './components/purchaseTicket';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';



class App extends React.Component {
	constructor(props){
		super(props)
  this.state = {
    events: [],
    isLoading: true,
    errors: null,
    data: []
	};
}



  // Now we're going to make a request for data using axios
  getPosts() {
    axios
			.get("http://localhost:3210/users")
      .then(response => {
        if(response.status >= 400) {
          throw new Error("error");
      }
      return response.json()
        console.log(response.data)
        const state = {...this.state}
        state.data = response.data
        this.setState(state)
        return axios
        .get("http://localhost:3210/data")
        .then(response => {
          return response.data.map(event=> ({
            event_id: `${event.event_id}`,
            id: `${event.id}`,
            cover_image: `${event.cover_image}`
          }))
        })
      })

      .then(events => {
        console.log(events)
        const state = {...this.state}
        state.events = events;
        state.isLoading = false
        // this.setState({
        //   events,
        //   isLoading: false,
        // });
        this.setState(state)
        console.log(this.state)
      })
      .catch(error => this.setState({ error, isLoading: false }));   
  }

  componentDidMount() {
    this.getPosts();
	}



  render() {
    const { isLoading, users , events} = this.state;

    return (
      <React.Fragment>
        <Router>
        <div>    
          {!isLoading ?
          (
            <Carousel showThumbs={false}>
                {	events && events.map(event=>{
                  let eventData = this.state.data.filter(e =>
                     (parseFloat(event.event_id) === e.id) && e.event_status==1 && Moment()<Moment(e.event_date))[0]
                  let combined
                  // console.log(eventData)
                  if (eventData.id === parseFloat(event.event_id)) return eventData = {...eventData, ...event}


                  return(
                    <React.Fragment>
                    <div key={event.id}>
                        <img className="mySlides img-responsive" src={event.cover_image} style={{width: "80%", height: 550}}/>
                          <Link to={{pathname: '/purchaseTicket',state: {myEvent: eventData} }}>

                            <button className="explore">Purchase Ticket</button>
                          </Link>
                          <button className="w3-button w3-black w3-display-left" onClick="plusDivs(-1)">&#10094;</button>
                          <button className="w3-button w3-black w3-display-right" onClick="plusDivs(1)">&#10095;</button>
                      </div>
                      </React.Fragment>
                  );

                })	 }

            </Carousel> )  :(

              <a href="http://dashboard.tickets4u.co.ke/index.php/login">
		            <img className="mySlides img-responsive" src="assets/images/default.jpg"/> 
              </a>

          )
            }
            <Switch>
              <Route exact path='/home'  />
              <Route path='/purchaseTicket' component={PurchaseTicket} />

          </Switch>
        </div>
        </Router>
      </React.Fragment>
    );
  }
}
export default App;
