import React from 'react';
import Moment from 'moment';
import axios from 'axios';


class App extends React.Component {
  state = {
    posts: [],
    isLoading: true,
    errors: null,
  };
  // Now we're going to make a request for data using axios
  getPosts() {
    axios
      .get("http://localhost:3210/users")
      .then(response => {
        return response.data.map(user=> ({
          event_status: `${user.event_status}`,
          id: `${user.id}`,
          event_venue: `${user.event_venue}`,
          event_date: `${user.event_date}`,
        }))
      })
      .then(users=>{
        var ev
        return axios
        .get("http://localhost:3210/data")
      })

      .then(users => {
        console.log(users)
        this.setState({
          users,
          isLoading: false,
        });
        // console.log(this.state)
      })
      .catch(error => this.setState({ error, isLoading: false }));   
  }

  componentDidMount() {
    this.getPosts();
  }

  render() {
    const { isLoading, users } = this.state;

    return (
      <React.Fragment>
        <h2>Random User</h2>
        <div>
          {!isLoading ? (
            users && users.map(user => {
              if(user.event_status==1 && Moment()<Moment(user.event_date)){
              return (
                <div key={user.id}>
                  <p>{user.event_status}</p>
                  <div>
                    <p>{user.event_venue}</p>
                  </div>
                  <p>{user.event_date}</p>
                  <hr />
                </div>
              );}
            })
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </React.Fragment>
    );
  }
}
export default App;
