import 'bootstrap/dist/css/bootstrap.min.css';
import logo from './logo.png';
import './App.css';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import PurchaseTicket from './components/aboutUs.components';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';



class App extends React.Component {
	constructor(props){
		super(props)
  this.state = {
    posts: [],
    isLoading: true,
    errors: null,
	};
	this.purchaseTicket=this.purchaseTicket.bind(this);
}
purchaseTicket(event){

}

  // Now we're going to make a request for data using axios
  getPosts() {
    axios
			.get("http://localhost:3210/users")
      .then(response => {
				console.log(response.data)
        return axios
        .get("http://localhost:3210/data")
        .then(response => {
          return response.data.map(event=> ({
            event_id: `${event.event_id}`,
            id: `${event.id}`,
            cover_image: `${event.cover_image}`
          }))
        })
      })

      .then(events => {
        console.log(events)
        this.setState({
          events,
          isLoading: false,
        });
        // console.log(this.state)
      })
      .catch(error => this.setState({ error, isLoading: false }));   
  }

  componentDidMount() {
    this.getPosts();
	}



  render() {
    const { isLoading, users , events} = this.state;

    return (
      <React.Fragment>
        <Router>
        <div>    
          {!isLoading ?
          (
            <Carousel>
                {	events && events.map(event=>{
                  return(

                    <div>
                        <img className="mySlides img-responsive" src={event.cover_image} style={{width: 1000, height: 500}}/>
                          <Link to={{pathname: '/purchaseTicket',state: [{event}]} }>
                            <button className="explore">Purchase Ticket</button>
                          </Link>
                          <button className="w3-button w3-black w3-display-left" onClick="plusDivs(-1)">&#10094;</button>
                          <button className="w3-button w3-black w3-display-right" onClick="plusDivs(1)">&#10095;</button>
                      </div>

                  );

                })	 }

            </Carousel> )  :(
            <div id="deskstop-slider">
              <a href="http://dashboard.tickets4u.co.ke/index.php/login">
		            <img className="mySlides img-responsive" src="assets/images/default.jpg"/> 
              </a>
            </div>
          )
						}
        </div>
        </Router>
      </React.Fragment>
    );
  }
}
export default App;
