import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';



class App extends React.Component {
	constructor(props){
		super(props)
  this.state = {
    posts: [],
    isLoading: true,
    errors: null,
	};
	this.purchaseTicket=this.purchaseTicket.bind(this);
}
