import React from 'react';
import axios from 'axios';


class App extends React.Component {
  state = {
    posts: [],
    isLoading: true,
    errors: null
  };
  // Now we're going to make a request for data using axios
  componentDidMount() {
    axios
      .get("http://localhost:3210/users")
      .then(response => {
        return response.data.map(user=> ({
          event_status: `${user.event_status}`,
          id: `${user.id}`,
          event_venue: `${user.event_venue}`,
          event_date: `${user.event_date}`

        }))
      })
      .then(users => {
        this.setState({
          users,
          isLoading: false
        });
      })
      .catch(error => this.setState({ error, isLoading: false }));
  }
  // Putting that data to use
  render() {
    const { isLoading, users } = this.state;
    var g1 = new Date(); 
    users && users.map(user =>{
      const { event_date } = user; 
    var g2 = new Date(event_date); 
     } )

    return (
      <React.Fragment>
        <h2>Random User</h2>
        <div>
          {!isLoading ? (
            users && users.map(user => {
              if(user.event_status==1)
              if (g1.getTime() < g2.getTime())
              return (
                <div key={user.id}>
                  <p>{user.event_status}</p>
                  <div>
                    <p>{user.event_venue}</p>
                  </div>
                  <p>{user.event_date}</p>
                  <hr />
                </div>
              );
            })
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </React.Fragment>
    );
  }
}
export default App;
